import {IERC20} from "./interfaces/IERC20.sol";

//assuming we can price Convex pools like UniV2 pools more than like Curve pools
contract ConvexOracleV2 {
    //address private constant BOOSTER = 0xF403C135812408BFbE8713b5A23a04b3D48AAE31; //convex deposit contract
    CurveOracleV2 private curve_oracle;

    //get total underlying in USD and divide by num of tokens
    //use external script to get pid by curve lp token
    constructor(CurveOracleV2 _curve_oracle) {
        curve_oracle = _curve_oracle;
    }

    //get curve lp tokens price, get convex lp token "depositToken", divide
    function get_convex_price(
        address curve_pool,
        uint256[] memory prices,
        uint16 pid,
        address convex_lp,
        address curve_lp
    ) external view returns (uint256) {
        uint256 curve_lp_price = curve_oracle.get_price(curve_pool, prices); //returns 1e36 scaled uint
        //	uint256 curve_supply = IERC20(curve_lp).totalSupply();
        //	uint256 convex_supply = IERC20(convex_lp).totalSupply();

        //	uint256 convex_price = calculate_convex_price(
        //		curve_lp_price,
        //		curve_supply,
        //		convex_supply
        //	);

        return curve_lp_price;
    }

    //not sure how vulnerable this is, check back later
    function calculate_convex_price(
        uint256 curve_lp_price,
        uint256 curve_supply,
        uint256 convex_supply
    ) internal pure returns (uint256) {
        return (curve_lp_price * curve_supply) / convex_supply;
    }
}
","keccak256":"0x95b0b98377e7d11a815ae7980022ccec1bd4c834c88a428ef74f5a84bd016391","license":"UNLICENSED"},"contracts/oracles/CurveOracleV2.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

import {ICurvePool} from "./interfaces/ICurvePoolV1.sol";
import {ICurveOracle} from "./interfaces/ICurveOracle.sol";
import {vMath} from "./libs/vMath.sol";

contract CurveOracleV2 is ICurveOracle {
    function get_price(address curve_pool, uint256[] memory prices)
        external
        view
        override
        returns (uint256)
    {
        uint256 virtual_price = ICurvePool(curve_pool).get_virtual_price();

        uint256 lp_price = calculate_v2_token_price(
            uint8(prices.length),
            virtual_price,
            prices
        );

        return lp_price;
    }

    //returns n_token * vp * (p1 * p2 * p3) ^1/n
    //n should only ever be 2, 3, or 4
    //returns the lp_price scaled by 1e36, so scale down by 1e36
    function calculate_v2_token_price(
        uint8 n,
        uint256 virtual_price,
        uint256[] memory prices
    ) internal pure returns (uint256) {
        uint256 product = vMath.product(prices);
        uint256 geo_mean = vMath.geometric_mean(n, product);
        return (n * virtual_price * geo_mean) / 10**18; //since virtual price has 18 decimals
    }
}
