        uint256 collateralCap;
        bool hasStrategy;
        bool usingGovernanceSetInterestRate; //if true, then the reserves that has this asset will
        uint256 governanceSetInterestRate;
    }

    struct InitReserveInputInternal {
        InitReserveInput input;
        uint64 trancheId;
    }

    enum ReserveAssetType {
        AAVE,
        CURVE
    } //update with other possible types of the underlying asset
    //AAVE is the original assets in the aave protocol
    //CURVE is the new LP tokens we are providing support for
    struct TrancheAddress {
        uint64 trancheId;
        address asset;
    }
    struct ReserveData {
        //stores the reserve configuration
        ReserveConfigurationMap configuration;
        //the liquidity index. Expressed in ray
        uint128 liquidityIndex; //not used for nonlendable assets
        //variable borrow index. Expressed in ray
        uint128 variableBorrowIndex; //not used for nonlendable assets
        //the current supply rate. Expressed in ray
        uint128 currentLiquidityRate; //deposit APR is defined as liquidityRate / RAY //not used for nonlendable assets
        //the current variable borrow rate. Expressed in ray
        uint128 currentVariableBorrowRate; //not used for nonlendable assets
        //the current stable borrow rate. Expressed in ray
        uint128 currentStableBorrowRate; //not used for nonlendable assets
        uint40 lastUpdateTimestamp;
        //tokens addresses
        address aTokenAddress;
        address stableDebtTokenAddress; //not used for nonlendable assets
        address variableDebtTokenAddress; //not used for nonlendable assets
        //address of the interest rate strategy
        address interestRateStrategyAddress; //not used for nonlendable assets
        //the id of the reserve. Represents the position in the list of the active reserves
        uint8 id;
        //maybe consider
        uint64 trancheId;
        uint256 collateralCap; //this can definitely be different per trancheId
        bool hasStrategy; //this might be put as a property of a reserve rather than property of the asset since USDC might have a trancheId that has a strategy, but unlikely to happen
        bool usingGovernanceSetInterestRate; //if true, then the reserves that has this asset will
        uint256 governanceSetInterestRate;
    }

    // uint8 constant NUM_TRANCHES = 3;

    struct ReserveConfigurationMap {
        //bit 0-15: LTV
        //bit 16-31: Liq. threshold
        //bit 32-47: Liq. bonus
        //bit 48-55: Decimals
        //bit 56: Reserve is active
        //bit 57: reserve is frozen
        //bit 58: borrowing is enabled
        //bit 59: stable rate borrowing enabled
        //bit 60-63: reserved
        //bit 64-79: reserve factor
        uint256 data;
    }

    struct UserConfigurationMap {
        uint256 data;
    }

    enum InterestRateMode {
        NONE,
        STABLE,
        VARIABLE
    }

    struct AcctTranche {
        address user;
        uint64 trancheId;
    }

    struct DepositVars {
        address asset;
        uint64 trancheId;
        address _addressesProvider;
        uint256 amount;
        address onBehalfOf;
        uint16 referralCode;
    }

    struct ExecuteBorrowParams {
        address asset;
        uint64 trancheId; //trancheId the user wants to borrow out of
        address user;
        address onBehalfOf;
        uint256 amount;
        uint256 interestRateMode;
        address aTokenAddress;
        uint16 referralCode;
        bool releaseUnderlying;
        uint256 _maxStableRateBorrowSizePercent;
        uint256 _reservesCount;
    }

    struct WithdrawParams {
        uint256 _reservesCount;
        address asset;
        uint64 trancheId;
        uint256 amount;
        address to;
    }

    struct calculateInterestRatesVars {
        address reserve;
        address aToken;
        uint256 liquidityAdded;
        uint256 liquidityTaken;
        uint256 reserveFactor;
    }

    struct flashLoanVars {
        address receiverAddress;
        address[] assets;
        uint64 trancheId;
        uint256[] amounts;
        uint256[] modes;
        address onBehalfOf;
        bytes params;
        uint16 referralCode;
        uint256 _flashLoanPremiumTotal;
        address oracle;
        uint256 _maxStableRateBorrowSizePercent;
        address _addressesprovider;
        // mapping(uint8 => uint256) _reservesCount;
    }
}

contracts/oracles/CurveOracleV2.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

import {ICurvePool} from "./interfaces/ICurvePoolV1.sol";
import {ICurveOracle} from "./interfaces/ICurveOracle.sol";
import {vMath} from "./libs/vMath.sol";

contract CurveOracleV2 is ICurveOracle {
    function get_price(address curve_pool, uint256[] memory prices)
        external
        view
        override
        returns (uint256)
    {
        uint256 virtual_price = ICurvePool(curve_pool).get_virtual_price();

        uint256 lp_price = calculate_v2_token_price(
            uint8(prices.length),
            virtual_price,
            prices
        );

        return lp_price;
    }

    //returns n_token * vp * (p1 * p2 * p3) ^1/n
    //n should only ever be 2, 3, or 4
    //returns the lp_price scaled by 1e36, so scale down by 1e36
    function calculate_v2_token_price(
        uint8 n,
        uint256 virtual_price,
        uint256[] memory prices
    ) internal pure returns (uint256) {
        uint256 product = vMath.product(prices);
        uint256 geo_mean = vMath.geometric_mean(n, product);
        return (n * virtual_price * geo_mean) / 10**18; //since virtual price has 18 decimals
    }
}

